import { gql, useMutation } from "@apollo/client";
import React,{useEffect} from "react";




const MUTATION_AVANCE = gql`
mutation Mutation($id: ID, $observaciones: String) {
  agregarObservacion(advance:{_id: $id, observaciones: $observaciones})
}
`
/* const Inscripcion = ({ inscripcion, refetch }) => {
    const [aprobarInscripcion, { data, loading, error }] = useMutation(APROBAR_INSCRIPCION);
  
    useEffect(() => {
      if (data) {
        toast.success('Inscripcion aprobada con exito');
        refetch();
      }
    }, [data]);
  
    useEffect(() => {
      if (error) {
        toast.error('Error aprobando la inscripcion');
      }
    }, [error]);
  
    const cambiarEstadoInscripcion = () => {
      aprobarInscripcion({
        variables: {
          aprobarInscripcionId: inscripcion._id,
        },
      });
    };
 */

const AgregarObservaciones = () => {
    const  [Mutation] = useMutation(MUTATION_AVANCE)

    let advance = {
        _id: "",
        observaciones: ""
    
        
    }



    return(<div>
        <h1>Crear observacion</h1>
        <form 
        onSubmit={e => {
            e.preventDefault();
            Mutation({variables: {
                _id: advance._id.value,
                observaciones: advance.observaciones.value, 
               
            }})

        }}>
            <div>
                <label >ID del Proyecto</label>
                <input ref = {_id => advance._id = _id} placeholder="Identificador del avance"/>
            </div>
            <div>
                <label>observaciones</label>
                <input ref = {observaciones => advance.observaciones = observaciones} placeholder="Agregar observacion"/>
            </div>
            
            <div>
                <button>Registrar observacion</button>
            </div>

        </form>


        </div>
    )
}













export default AgregarObservaciones
